import React, { useState, useEffect } from "react";
import profileImage from "../assets/images/profile_image.png";

export default function LandingPage() {
  const [visibleText, setVisibleText] = useState("");

  useEffect(() => {
    const textToType = "Software Developer, Front-end Web, and Mobile Designer.";
    let index = 0;

    const typingInterval = setInterval(() => {
      setVisibleText(textToType.slice(0, index));
      index++;

      if (index > textToType.length) {
        clearInterval(typingInterval);
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, []);

  return (
    <>
      <div className="flex items-center h-screen">
        <div className="text-white p-4 pl-20">
          {/* Header */}
          <p className="text-4xl font-bold">Hi!</p>
          <p className="text-3xl font-bold">My name is Emmanuel Kasuti</p>
          <p className="overflow-hidden whitespace-no-wrap"> I'm a {visibleText}</p>
        </div>
      </div>
    </>
  );
}
