import React, { useState, useEffect } from "react";
import profileImage from "../assets/images/profile_image.png"; // Adjust the path accordingly

export default function LandingPage() {
  const [visibleText, setVisibleText] = useState("");

  // useEffect phrases: Roles
  useEffect(() => {
    const phrases = [
      "Software Developer,",
      "Front-end Developer,",
      "Mobile Designer",
      "Pencil Artist.",
    ];

    let currentPhraseIndex = 0;
    let index = 0;
    let isTyping = true;

    const typingInterval = setInterval(() => {
      setVisibleText(phrases[currentPhraseIndex].slice(0, index));

      if (isTyping) {
        index++;
        if (index > phrases[currentPhraseIndex].length) {
          isTyping = false;
          setTimeout(() => {
            isTyping = true;
            index = 0;
            currentPhraseIndex = (currentPhraseIndex + 1) % phrases.length;
          }, 1000);
        }
      } else {
        if (index === 0) {
          isTyping = true;
          currentPhraseIndex = (currentPhraseIndex + 1) % phrases.length;
        } else {
          index--;
        }
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, []);

  return (
    <>
      {/* HomePage - Intro */}
      <div className="flex justify-center items-center h-screen">
        <div className="text-white">
          {/* Header */}
          <p className="text-6xl font-bold flex justify-center">Hi!</p>
          <p className="text-6xl font-bold mt-4">I'm Emmanuel Kasuti</p>
          <div
            className="text-2xl overflow-hidden whitespace-no-wrap mt-4 flex justify-center "
            style={{ height: "50px" }}
          >
            {visibleText}
          </div>
        </div>
      </div>

      {/* AboutMe - Section */}
      <div className="flex  justify-center pl-20 h-screen">
        <div>
          {/* Image on the left */}
        </div>
        <div className="text-white flex flex-col justify-right">
          {/* Get to Know me paragraph */}
          <p className="text-2xl font-bold ">Get to know me</p>
          <p>
            My name is Emmanuel Kasuti and I am a Sophomore at Dartmouth College
            from Kenya. Fun fact about me is that I play the saxophone and I am
            an artist too! <br></br>
            I’m currently pursuing a degree in Computer science
            modified with digital arts. With a focus on creative problem solving
            and innovation. <br></br> I’m excited to learn more about how technology can
            be used to shape our world. I enjoy traveling, exploring new places
            and playing the saxophone. <br></br> I also enjoy spending time in the
            kitchen, experimenting with new recipes and cooking for my friends
            and family.
          </p>
          {/* Button to view portfolio */}
        </div>
      </div>
    </>
  );
}
