import React from 'react';
import { useHistory } from 'react-router-dom';
import CustomButton from './CustomButton'; // Adjust the path based on your project structure

const AboutMeSection = ({ profileImage }) => {
  const history = useHistory();

  const handleViewPortfolioClick = () => {
    // Navigate to the '/gallery' route
    history.push('/gallery');
  };

  return (
    <div className="flex pl-20 h-screen">
      {/* Green Rectangle & profile image */}
      <div className="mr-20">
        <div className="ml-auto w-150 h-150 bg-green-500 rounded-bl-full rounded-tl-full rounded-br-full">
          {/* Image on the left */}
          <img
            src={profileImage}
            alt="profile image"
            className="w-100 h-150 rounded-bl-full rounded-br-full"
          />
        </div>
      </div>

      {/* Get to Know me paragraph */}
      <div className="text-white flex flex-col pr-20">
        <p className="text-3xl font-bold flex justify-center">
          GET TO KNOW ME
        </p>
        <p className="text-xl mt-4" style={{ maxWidth: '600px' }}>
          My name is Emmanuel Kasuti. I am a Sophomore at Dartmouth College
          from Kenya. 
          I’m currently pursuing a degree in Computer science
          modified with digital arts. With a focus on creative problem solving
          and innovation. I’m excited to learn more about how technology can
          be used to shape our world
        </p>

        {/* Button to view portfolio */}
        <CustomButton
          text="View Portfolio"
          onClick={handleViewPortfolioClick}
          backgroundColor="#FFFF00"
        />
      </div>
    </div>
  );
};

export default AboutMeSection;
