import React, { useState } from "react";
import PrimaryButton from "../components/Button";
import InputForm from "../components/InputForm";

function ContactPage() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    description: "",
  });

  const handleInputChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
  };

  // const handleLinkClick = () => {
  //   console.log("Send message", formData);
  //   // Here you can use formData to send data to the backend
  // };

  return (
    <>
      <div className="w-full max-w-screen-xl mx-auto p-4 md:py-10">
        {/* Page Title */}
        <div className="text-white p-4 pl-20 text-xl flex justify-center">
          Contact Me
        </div>

        {/* Content */}
        <div className="flex">
          {/* Input form on the left */}
          <InputForm
            outerName="Name (Outside Form)"
            suggestedName="Suggested Name"
            onInputChange={handleInputChange}
          />

          {/* Get in Touch on the right */}
          <div className="text-white ml-8">Get in Touch</div>
        </div>
      </div>
    </>
  );
}

export default ContactPage;
