// InputForm.js
import React from "react";
import PropTypes from "prop-types";

const InputForm = ({ outerName, suggestedName, onInputChange }) => {
  return (
    <div className="text-white border border-yellow-500 w-1/2 p-6 h-screen">
      <div className="m-6">
        {/* Name (outside the form) */}
        <div className="border mt-4">{outerName}</div>

        {/* Name (inside the form) */}
        <div className="border mt-4">
          <input
            type="text"
            placeholder={suggestedName}
            className="w-full p-2"
            onChange={(e) => onInputChange("name", e.target.value)}
          />
        </div>

        {/* Email */}
        <div className="border mt-4">
          <input
            type="email"
            placeholder="Email"
            className="w-full p-2"
            onChange={(e) => onInputChange("email", e.target.value)}
          />
        </div>

        {/* Description */}
        <div className="border mt-4">
          <textarea
            placeholder="Description"
            rows="4"
            className="w-full p-2"
            onChange={(e) => onInputChange("description", e.target.value)}
          />
        </div>

        {/* Button to send */}
        <div className="mt-4">
          <PrimaryButton
            text="SEND"
            onClick={handleLinkClick}
            backgroundColor="#FFFF00"
            width="200px"
          />
        </div>
      </div>
    </div>
  );
};

InputForm.propTypes = {
  outerName: PropTypes.string.isRequired,
  suggestedName: PropTypes.string.isRequired,
  onInputChange: PropTypes.func.isRequired,
};

export default InputForm;
