// InputForm.jsx

import React from 'react';

const InputForm = ({ suggestedName, getHeight }) => {
  return (
    <input
      type="text"
      placeholder={suggestedName}
      style={{ height: getHeight ? getHeight() + 'px' : 'auto' }} 
      className="w-full bg-gray-300 rounded p-2 mb-4"
    />
  );
};

export default InputForm;
