import React from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
    const navStyle = {
        background: 'black',
        color: 'white',
        padding: '20px',
        alignItems: 'center',
        display: "flex",
        justifyContent: "space-between",
    }

    const logoStyle ={
        fontSize: '1.5rem',
        fontWeight: 'bold',
        textDecoration: 'none',
        color: 'white',
        display: "flex",
        justifyContent: "space-between",

    }

    return (
        <nav className="flex justify-between items-center bg-black text-white p-4">
            <Link to="/" className="text-white text-xl font-bold">KREATIVE KASUTI</Link>
            <div className="flex gap-4">
                <Link to="/">HOME</Link>
                <Link to="/gallery">GALLERY</Link>
                <Link to="/contact">CONTACT</Link>
                <Link to="/shop">SHOP</Link>
            </div>
        </nav>
    )
}

export default NavBar;
