import React from "react";
import PropTypes from "prop-types";

const PictureFrame = ({ imageUrl, altText, title, category }) => {
  return (
    <div className="relative group">
      <div className="flex items-center justify-center">
        <div className="bg-gray-800 p-6 w-80 h-150 relative group-hover:bg-yellow-300 transition-colors">
          <div className="bg-white p-4">
            <img
              src={imageUrl}
              alt={altText}
              className="block mx-auto max-w-full max-h-full"
            />
          </div>
        </div>
      </div>

      {/* Frame title and category (visible on hover) */}
      <div className="group-hover:opacity-100 opacity-0 absolute bottom-0 right-0 p-4 text-white pr-12 pb-10 transition-opacity duration-300">
        <div className="text-xl font-bold">{title}</div>
        <div className="text-right">{category}</div>
      </div>
    </div>
  );
};

PictureFrame.propTypes = {
  imageUrl: PropTypes.string.isRequired,
  altText: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  category: PropTypes.string.isRequired,
};

export default PictureFrame;
