import React from "react";
import profileImage from "../assets/images/profile_image.png";

function GalleryPage() {
  return (
    <>
      <div className=" pl-20 ">
        <div className="text-white p-4 text-xl">GalleryPage</div>
        {/* Categories of art: Portraits, Graphite, Charcoal, Color Pencil, Digital */}
        <div className="flex flex-row justify-center">
          {[
            "  All  ",
            "Portraits",
            "Graphite",
            "Charcoal",
            "Color Pencil",
            "Digital",
          ].map((category) => (
            <div
              key={category}
              className="flex flex-row items-center text-white m-1"
            >
              <span className="">--</span>
              <div className="border-l-2 pl-1 h-4"></div>
              <div className="border pl-4 pr-4">{category}</div>
            </div>
          ))}
        </div>

        {/* Have a frame for each photo, a white window mat and the portrait in the center */}
        <div>
        <div className="mr-20">
          <div className="ml-auto w-150 h-150 bg-yellow-500 rounded-bl-full rounded-tl-full rounded-br-full">
            {/* Image on the left */}
            <img
              src={profileImage}
              alt="profile"
              className="w-100 h-150 rounded-bl-full rounded-br-full"
            />
          </div>
        </div>
        </div>
      </div>
    </>
  );
}

export default GalleryPage;
