import React from "react";
import PictureFrame from "../components/picture_frame";

// Image imports
import darkWall from "../assets/images/dark_wall.png";
import { artportfolioData } from "../data/PortfolioData";
import { artCategory } from "../data/PortfolioData";

function GalleryPage() {
  const { myPortfolioData } = portfolioData;
  const { myArtCategory } = artCategory;
  return (
    <>
      <div className="pl-20">
        <div className="text-white p-4 text-xl">GalleryPage</div>
        {/* Categories of art: Portraits, Graphite, Charcoal, Color Pencil, Digital */}
        <div className="flex flex-row justify-center">
          {myArtCategory.map((category) => (
            <div key={category.id} className="flex flex-row items-center text-white m-1">
              <span className="">--</span>
              <div className="border-l-2 pl-1 h-4"></div>
              <div className="border pl-4 pr-4">{category.category}</div>
            </div>
          ))}
        </div>
      </div>
      <div className="m-8 bg-gallery-image h-screen flex justify-between pt-20" style={{ backgroundImage: `url(${darkWall})`, backgroundSize: "cover" }}>
        {/* Map the frames from portfolio data */}
        <div className="pl-40 flex justify-between">

          {/* Two frames on the left */}
          <div className="flex flex-col mr-8">
            {myPortfolioData.slice(0, 2).map((art) => (
              <div key={art.id} className="mb-4">
                <PictureFrame
                  imageUrl={art.imageUrl}
                  altText={art.altText}
                  frameType={art.frameType}
                />
              </div>
            ))}
          </div>

          {/* One frame in the middle */}
          <div className="flex flex-col mt-6">
          {myPortfolioData.slice(2, 3).map((art) => (
              <div key={art.id} className="">
                <PictureFrame
                  imageUrl={art.imageUrl}
                  altText={art.altText}
                  frameType={art.frameType}
                />
              </div>
            ))}
          </div>

           {/* Two frames on the right */}
           <div className="flex flex-col ml-8">
            {myPortfolioData.slice(3, 5).map((art) => (
              <div key={art.id} className="mb-4">
                <PictureFrame
                  imageUrl={art.imageUrl}
                  altText={art.altText}
                  frameType={art.frameType}
                />
              </div>
            ))}
          </div>

        </div>
      </div>
    </>
  );
}

export default GalleryPage;
