import React, { useState, useEffect } from "react";

export default function LandingPage() {
  const [visibleText, setVisibleText] = useState("");

  // useEffect phrases: Roles
  useEffect(() => {
    const phrases = [
      "Software Developer,",
      "Front-end Web,",
      "Mobile Designer."
    ];

    let currentPhraseIndex = 0;
    let index = 0;
    let isTyping = true;

    const typingInterval = setInterval(() => {
      setVisibleText(phrases[currentPhraseIndex].slice(0, index));

      if (isTyping) {
        index++;
        if (index > phrases[currentPhraseIndex].length) {
          isTyping = false;
          setTimeout(() => {
            isTyping = true;
            index = 0;
            currentPhraseIndex = (currentPhraseIndex + 1) % phrases.length;
          }, 1000); 
        }
      } else {
        if (index === 0) {
          isTyping = true;
          currentPhraseIndex = (currentPhraseIndex + 1) % phrases.length;
        } else {
          index--;
        }
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, []);

  return (
    <>
    {/* HomePage - Intro */}
      <div className="flex justify-center items-center h-screen">
        <div className="text-white">
          {/* Header */}
          <p className="text-6xl font-bold flex justify-center">Hi!</p>
          <p className="text-6xl font-bold mt-4">I'm Emmanuel Kasuti</p>
          <div className="text-2xl overflow-hidden whitespace-no-wrap mt-4 flex justify-center " style={{ height: "50px" }}>
            {visibleText}
          </div>
        </div>
      </div>

      {/* AboutMe - Section */}

    </>
  );
}
