import React from 'react';

const Button = ({ text, onClick, color, backgroundColor, borderRadius, width }) => {
  const buttonStyle = {
    color: color || '#000000',
    backgroundColor: backgroundColor || '#FFFF00',
    borderRadius: borderRadius || '10px',
    padding: '10px 8px',
    cursor: 'pointer',
    width: width || 'auto', // Set the width to 'auto' or specify a specific value
  };

  return (
    <button style={buttonStyle} onClick={onClick}>
      {text}
    </button>
  );
};

export default Button;
