import React from 'react';

const CustomButton = ({ text, onClick, color, backgroundColor, borderRadius }) => {
  const buttonStyle = {
    color: color || '#ffffff',
    backgroundColor: backgroundColor || '#FFFF11',
    borderRadius: borderRadius || '5px', 
    padding: '10px 20px',
    cursor: 'pointer',
  };

  return (
    <button style={buttonStyle} onClick={onClick}>
      {text}
    </button>
  );
};

export default CustomButton;
