import React, { useState, useEffect } from "react";

export default function LandingPage() {
  const [visibleText, setVisibleText] = useState("");
  const roles = ["Software Engineer", "Front-end Web Designer", "Mobile Designer"];
  let roleIndex = 0;
  let forwardTyping = true;
  let textIndex = 0;

  useEffect(() => {
    const typingInterval = setInterval(() => {
      const currentRole = roles[roleIndex];

      if (forwardTyping) {
        setVisibleText(currentRole.slice(0, textIndex));
        textIndex++;

        if (textIndex > currentRole.length) {
          forwardTyping = false;
          textIndex = currentRole.length;
        }
      } else {
        setVisibleText(currentRole.slice(0, textIndex));
        textIndex--;

        if (textIndex < 0) {
          forwardTyping = true;
          textIndex = 0;

          roleIndex = (roleIndex + 1) % roles.length;
        }
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, [roles]);

  return (
    <div className="flex justify-center items-center h-screen text-white">
      <div>
        {/* Header */}
        <p className="text-6xl font-bold text-center">Hi!</p>
        <p className="text-6xl font-bold mt-4 text-center">I'm Emmanuel Kasuti</p>
        <p className="text-2xl overflow-hidden whitespace-no-wrap mt-4 text-center">
          {" "}
          {visibleText}
        </p>
      </div>
    </div>
  );
}
