import React from "react";
import PropTypes from "prop-types";

// Portrait Frame
const PictureFrame = ({ imageUrl, altText, frameType }) => {
  const frameClasses = `bg-gray-800 p-6 ${
    frameType === "Portrait" ? "w-80 h-150" : "w-150 h-80"
  }`;

  return (
    <div className="flex items-center justify-center">
      <div className={frameClasses}>
        <div className="bg-white p-4">
          <img
            src={imageUrl}
            alt={altText}
            className="block mx-auto max-w-full max-h-full"
          />
        </div>
      </div>
    </div>
  );
};

PictureFrame.propTypes = {
  imageUrl: PropTypes.string.isRequired,
  altText: PropTypes.string.isRequired,
  frameType: PropTypes.oneOf(["Portrait", "Landscape"]).isRequired,
};

export default PictureFrame;
