import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

function NavBar() {
    const [activeLink, setActiveLink] = useState('/');
    const location = useLocation();

    const handleLinkClick = (path) => {
        setActiveLink(path);
    };

    return (
        <nav className="flex justify-between items-center bg-black text-white p-4">
            <Link
                to="/"
                className={`text-white text-xl font-bold `}
                onClick={() => handleLinkClick('/')}
            >
                Kreative Kasuti
            </Link>
            <div className="text-sm font-bold flex gap-4">
                <Link
                    to="/"
                    className={`text-white ${activeLink === '/' && 'border-b-2 border-red-500'}`}
                    onClick={() => handleLinkClick('/')}
                >
                    HOME
                </Link>
                <Link
                    to="/aboutme"
                    className={`text-white ${activeLink === '/aboutme' && 'border-b-2 border-red-500'}`}
                    onClick={() => handleLinkClick('/aboutme')}
                >
                    ABOUTME
                </Link>
                <Link
                    to="/gallery"
                    className={`text-white ${activeLink === '/gallery' && 'border-b-2 border-red-500'}`}
                    onClick={() => handleLinkClick('/gallery')}
                >
                    GALLERY
                </Link>
                <Link
                    to="/contact"
                    className={`text-white ${activeLink === '/contact' && 'border-b-2 border-red-500'}`}
                    onClick={() => handleLinkClick('/contact')}
                >
                    CONTACT
                </Link>
                <Link
                    to="/shop"
                    className={`text-white ${activeLink === '/shop' && 'border-b-2 border-red-500'}`}
                    onClick={() => handleLinkClick('/shop')}
                >
                    SHOP
                </Link>
            </div>
        </nav>
    );
}

export default NavBar;
