import React from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
    const navStyle = {
        background: 'black',
        color: 'white',
        padding: '20px',
        alignItems: 'center',
        display: "flex",
        alignItems: "center",
    }

    const logoStyle ={
        fontSize: '1.5rem',
        fontWeight: 'bold',
        textDecoration: 'none',
        color: 'white',

    }

    return (
        <nav style={navStyle} >
            <Link to="/" style={logoStyle}>KREATIVE KASUTI</Link>
            <div className='flex justify-between'>
                <Link to="/">HOME</Link>
                <Link to="/gallery">GALLERY</Link>
                <Link to="/contact">CONTACT</Link>
                <Link to="/shop">SHOP</Link>
            </div>

        </nav>
    )
}

export default NavBar;
