import React, { useState, useEffect } from "react";
import profileImage from "../assets/images/profile_image.png";

export default function LandingPage() {
  const [visibleText, setVisibleText] = useState("");
  const roles = [
    "Software Developer",
    "Front-end Web Designer",
    "Mobile Designer",
  ];

  useEffect(() => {
    let roleIndex = 0;
    let textIndex = 0;

    const typingInterval = setInterval(() => {
      const currentRole = roles[roleIndex];
      setVisibleText(currentRole.slice(0, textIndex));
      textIndex++;

      if (textIndex > currentRole.length) {
        textIndex = 0;
        roleIndex = (roleIndex + 1) % roles.length;
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, [roles]);

  return (
    <>
      <div className="flex items-center h-screen">
        <div className="text-white p-4 pl-20">
          {/* Header */}
          <p className="text-2xl font-bold">Hi!</p>
          <p className="text-3xl font-bold">My name is Emmanuel Kasuti</p>
          <p className="overflow-hidden whitespace-no-wrap">
            {" "}
            I'm a {visibleText}
          </p>
        </div>
      </div>
    </>
  );
}
